import React, { useState, useEffect } from "react";
import { io } from "socket.io-client";

const socket = io("http://localhost:5000");

function Messaging({ conversationId, receiverId }) {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [userId, setUserId] = useState(""); // Current logged-in user ID

  useEffect(() => {
    // Retrieve the logged-in user's ID (e.g., from localStorage or a context provider)
    const loggedInUser = localStorage.getItem("userId"); // Replace with your authentication logic
    if (loggedInUser) {
      setUserId(loggedInUser);
      socket.emit("joinRoom", conversationId);
    }
  }, [conversationId]);

  useEffect(() => {
    // Fetch existing messages for the conversation
    fetch(`http://localhost:5000/api/messages/${conversationId}`)
      .then((response) => response.json())
      .then((data) => setMessages(data))
      .catch((error) => console.error("Error fetching messages:", error));
  }, [conversationId]);

  useEffect(() => {
    // Listen for real-time incoming messages
    socket.on("receiveMessage", (message) => {
      setMessages((prev) => [...prev, message]);
    });

    return () => {
      socket.off("receiveMessage");
    };
  }, []);

  const handleSendMessage = () => {
    const message = {
      conversationId,
      senderId: userId,
      receiverId,
      content: newMessage,
    };

    // Emit message to the server
    socket.emit("sendMessage", message);

    // Update the UI optimistically
    setMessages([...messages, { ...message, senderId: userId }]);
    setNewMessage("");
  };

  return (
    <div>
      <h3>Messaging</h3>
      <ul>
        {messages.map((msg, index) => (
          <li key={index}>
            <strong>{msg.senderId === userId ? "You: " : "Friend: "}</strong>
            {msg.content}
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={newMessage}
        onChange={(e) => setNewMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={handleSendMessage}>Send</button>
    </div>
  );
}

export default Messaging;
